import jsonfrom pygal_maps_world.maps import Worldfrom pygal.style import LightColorizedStyle as LCS,RotateStyle as RSfrom country_codes import get_country_code#将数据加载到一个列表中file_name = "Document/population_data.json"with open(file_name) as f:    pop_date = json.load(f)#打印每个国家2010年人口数量,创建一个包含人口数量的字典cc_populations = {}for pop_dict in pop_date:    if pop_dict["Year"] == "2010":        country_name = pop_dict["Country Name"]        #Python不能直接将包含小数点的字符串'1127437398.85751' 转换为整数        #为消除这种错误，我们先将字符串转换为浮点数，再将浮点数转换为整数        population = int(float(pop_dict["Value"]))        code = get_country_code(country_name)        if code:            cc_populations[code] = population        else:            print("ERROR - " + country_name)#根据人口数量将所有国家分为三组cc_pops_1, cc_pops_2, cc_pops_3 = {}, {}, {}for cc, pop in cc_populations.items():    if pop < 10000000:        cc_pops_1[cc] = pop    elif pop < 1000000000:        cc_pops_2[cc] = pop    else:        cc_pops_3[cc] = pop#查看每组包含多少国家print(len(cc_pops_1),len(cc_pops_2),len(cc_pops_3))wm_style = RS("#336699",base_style=LCS)wm = World(style=wm_style)wm.title = "World population in 2010, by country"wm.add("0-10m",cc_pops_1)wm.add("10m-1bn",cc_pops_2)wm.add(">1bn",cc_pops_3)wm.render_to_file("World population.svg")